import $!{tableInfo.savePackageName}.common.enums.IExceptionEnum;

#classComment("业务异常断言接口") 
public interface ServiceExceptionAssert extends IExceptionEnum, Assert{

    /**
     * 创建异常
     *
     * @return {@link RuntimeException}
     */
    @Override
    default RuntimeException newException(){
        return new ServiceException(this);
    }

    /**
     * 创建异常
     *
     * @param code 错误码
     * @param msg 错误提示
     * @return {@link RuntimeException}
     */
    @Override
    default RuntimeException newException(int code, String msg){
        return new ServiceException(code, msg);
    }
}

##引入宏定义 $!define ##设置回调 $!callback.setFileName("ServletResponseStatusEnum.java") $!callback.setSavePath($tool.append($tableInfo.savePath, "/common/enums")) ##使用宏定义设置包后缀 #setPackageSuffix("common.enums")
import lombok.AllArgsConstructor; import lombok.Getter; import org.springframework.http.HttpStatus;
import java.util.Objects; import java.util.stream.Stream;
#classComment("servlet异常和状态码枚举") @Getter @AllArgsConstructor public enum ServletResponseStatusEnum {
HttpRequestMethodNotSupportedException("HttpRequestMethodNotSupportedException", HttpStatus.METHOD_NOT_ALLOWED.value()), HttpMediaTypeNotSupportedException("HttpMediaTypeNotSupportedException", HttpStatus.UNSUPPORTED_MEDIA_TYPE.value()), HttpMediaTypeNotAcceptableException("HttpMediaTypeNotAcceptableException", HttpStatus.NOT_ACCEPTABLE.value()), MissingPathVariableException("MissingPathVariableException", HttpStatus.INTERNAL_SERVER_ERROR.value()), MissingServletRequestParameterException("MissingServletRequestParameterException", HttpStatus.BAD_REQUEST.value()), ServletRequestBindingException("ServletRequestBindingException", HttpStatus.BAD_REQUEST.value()), ConversionNotSupportedException("ConversionNotSupportedException", HttpStatus.INTERNAL_SERVER_ERROR.value()), TypeMismatchException("TypeMismatchException", HttpStatus.BAD_REQUEST.value()), HttpMessageNotReadableException("HttpMessageNotReadableException", HttpStatus.BAD_REQUEST.value()), HttpMessageNotWritableException("HttpMessageNotWritableException", HttpStatus.INTERNAL_SERVER_ERROR.value()), MethodArgumentNotValidException("MethodArgumentNotValidException", HttpStatus.BAD_REQUEST.value()), MissingServletRequestPartException("MissingServletRequestPartException", HttpStatus.BAD_REQUEST.value()), NoHandlerFoundException("NoHandlerFoundException", HttpStatus.NOT_FOUND.value()), AsyncRequestTimeoutException("AsyncRequestTimeoutException", HttpStatus.SERVICE_UNAVAILABLE.value()), MethodArgumentTypeMismatchException("MethodArgumentTypeMismatchException", HttpStatus.BAD_REQUEST.value()), ;
private final String className; private final Integer status;
public static ServletResponseStatusEnum of(String className){ Objects.requireNonNull(className);
return Stream.of(values()) .filter(bean -> bean.getClassName().equals(className)) .findAny() .orElseThrow(() -> new IllegalArgumentException(className + " not exists")); } }

