import $!{tableInfo.savePackageName}.common.enums.ExceptionEnum;
import lombok.extern.slf4j.Slf4j;
import org.springframework.boot.web.servlet.error.ErrorController;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.RequestMapping;

import javax.servlet.http.HttpServletRequest;

#classComment("默认错误处理控制器") 
@Slf4j
@Controller
public class DefaultErrorController implements ErrorController {

    @RequestMapping("/error")
    public String handleError(HttpServletRequest request){
        //获取statusCode:401,404,500
        Integer statusCode = (Integer) request.getAttribute("javax.servlet.error.status_code");
        String message = (String) request.getAttribute("javax.servlet.error.message");
        log.error("[DefaultErrorController]javax.servlet.error.message:{}", message);
        if(statusCode == 500){
            throw new ServiceException(ExceptionEnum.INTERNAL_SERVER_ERROR);
        }else if(statusCode == 404){
            throw new ServiceException(404, "Not Found");
        }else if(statusCode == 403){
            throw new ServiceException(403, "Forbidden");
        }else{
            throw new ServiceException(ExceptionEnum.INTERNAL_SERVER_ERROR);
        }
    }

    @Override
    public String getErrorPath() {
        return "/error";
    }
}

