import org.springframework.web.servlet.mvc.method.annotation.ResponseEntityExceptionHandler;
import org.springframework.web.util.WebUtils;

import javax.validation.ConstraintViolationException;
import java.nio.file.AccessDeniedException;
import java.util.List;
import java.util.stream.Collectors;

#classComment("统一异常处理类") 
@Slf4j
@RestControllerAdvice
public class GlobalExceptionHandler extends ResponseEntityExceptionHandler {

    /**
     * 处理单个参数校验异常
     *
     * @param e ConstraintViolationException
     * @return ResponseEntity
     */
    @ExceptionHandler(ConstraintViolationException.class)
    public ResponseEntity<Object> handleConstraintViolationException(ConstraintViolationException e) {
        String message = e.getMessage();
        message = StringUtils.substringAfterLast(message,":").trim();
        log.error("[统一异常]单个参数校验异常，异常信息:{}",message);

        // 生成返回结果
        return createResponseResponseEntity(message, HttpStatus.BAD_REQUEST.value());
    }

    /**
     * 自定义异常处理
     * @param ex
     * @param body
     * @param headers
     * @param status
     * @param request
     * @return
     */
    @Override
    protected ResponseEntity<Object> handleExceptionInternal(
            Exception ex, @Nullable Object body, HttpHeaders headers, HttpStatus status, WebRequest request) {
        String msg="";
        if(ex instanceof BindException){
            //处理普通类型Content-Type参数校验异常
            List<FieldError> fieldErrors = ((BindException)ex).getFieldErrors();
            msg = fieldErrors.stream().map(DefaultMessageSourceResolvable::getDefaultMessage)
                    .collect(Collectors.joining("|"));
            log.error("[统一异常]对象参数校验异常，异常信息:{}", msg);
        }else if(ex instanceof MethodArgumentNotValidException) {
            //处理Content-Type=application/json的参数校验异常
            List<FieldError> fieldErrors = ((MethodArgumentNotValidException) ex).getBindingResult().getFieldErrors();
            msg = fieldErrors.stream().map(DefaultMessageSourceResolvable::getDefaultMessage)
                    .collect(Collectors.joining("|"));
            log.error("[统一异常]对象参数校验异常，异常信息:{}",msg);
        } else if (ex instanceof HttpMessageNotReadableException){
            //处理请求体为空异常
            String message = ex.getMessage();
            log.error("[统一异常]参数错误异常，异常信息，{}", message);
            if(message.contains("JSON parse error")) {
                String paramName = StringUtils.substringBetween(message, "[\"", "\"]");
                msg = paramName + ": " + StringUtils.substring(message,
                        message.indexOf("problem:") + 8, message.indexOf(";")).trim();
            } else if (message.contains("Required request body is missing")){
                msg = StringUtils.substringBefore(message,":").trim();
            } else {
                msg = StringUtils.substringBefore(message,";").trim();
            }
        }else {
            //其他异常
            msg = ex.getMessage();
        }

        if (HttpStatus.INTERNAL_SERVER_ERROR.equals(status)) {
            request.setAttribute(WebUtils.ERROR_EXCEPTION_ATTRIBUTE, ex, WebRequest.SCOPE_REQUEST);
        }
        log.error("[统一异常]{}异常，异常信息:{}", ex.getClass().getSimpleName(), msg);
        return createResponseResponseEntity(msg, status.value());
    }

    /**
     * 处理403异常
     *
     * @param e AccessDeniedException
     * @return ResponseEntity
     */
    @ExceptionHandler(AccessDeniedException.class)
    public ResponseEntity<Object> handleAccessDeniedException(AccessDeniedException e) {
        String message = e.getMessage();
        log.error("[统一异常]拒绝访问异常，异常信息，{}", message);

        // 生成返回结果
        return createResponseResponseEntity(message, HttpStatus.FORBIDDEN.value());
    }

    /**
     * 处理自定义业务异常
     *
     * @param cause 异常
     * @param e ServiceException
     * @return ResponseEntity
     */
    @ExceptionHandler(ServiceException.class)
    public ResponseEntity<Object> handleException(Throwable cause, ServiceException e){
        String message;
        int code;
        IExceptionEnum exceptionEnum = e.getExceptionEnum();
        if(null != exceptionEnum){
            message = exceptionEnum.getMessage();
            code = exceptionEnum.getCode();
        }else {
            message = e.getMessage();
            code = e.getCode();
        }
        log.error("[统一异常]ServiceException异常，异常信息:{}", message);

        // 记录堆栈信息
        final Writer result = new StringWriter();
        final PrintWriter printWriter = new PrintWriter(result);
        cause.printStackTrace(printWriter);
        log.error("[统一异常]其他异常，堆栈信息：{}", result);

        // 生成返回结果
        return Result.error(code, message);
    }

    /**
     * 其他异常统一处理
     *
     * @param cause 异常
     * @param e Exception
     * @return ResponseEntity
     */
    @ExceptionHandler(Exception.class)
    public ResponseEntity<Object> exceptionHandler(Throwable cause, Exception e) {
        log.error("[统一异常]其他异常，异常:{}", e.getClass().toString());
        String message = e.getMessage();
        log.error("[统一异常]其他异常，异常信息，{}", message);

        // 记录堆栈信息
        final Writer result = new StringWriter();
        final PrintWriter printWriter = new PrintWriter(result);
        cause.printStackTrace(printWriter);
        log.error("[统一异常]其他异常，堆栈信息：{}", result);

        // 生成返回结果
        return Result.error(HttpStatus.INTERNAL_SERVER_ERROR.value(), message);
    }

    /**
     * 返回异常处理结果
     *
     * @param msg 异常信息
     * @param status 状态码
     * @return ResponseEntity
     */
    private ResponseEntity<Object> createResponseResponseEntity(String msg, int status) {
        // 生成返回结果
        Result<Object> result = Result.error(status, msg);
        return ResponseEntity.status(status).body(result);
    }
}

