import com.fasterxml.jackson.annotation.JsonInclude;
import $!{tableInfo.savePackageName}.common.enums.ResultCodeEnum;
import lombok.Data;
import java.util.HashMap;
import java.util.Map;

#classComment("响应结果封装实体") 
@Data
@JsonInclude(JsonInclude.Include.NON_NULL)
public class Result<T> {

    private boolean success;//是否成功
    private Integer code;//返回码
    private String message;//返回消息
    private T data;//返回数据

    private Map<String,Object> extend;//扩展数据

    public Result() {
    }

    public Result(boolean flag, Integer code, String message, T data) {
        this.success = flag;
        this.code = code;
        this.message = message;
        this.data = data;
    }

    public Result(boolean flag, Integer code, String message) {
        this.success = flag;
        this.code = code;
        this.message = message;
    }

    /**
     * 响应成功
     *
     * @param message 提示信息
     * @return Result<Void>
     */
    public static Result<Void> success(String message){
        return new Result<>(true, ResultCodeEnum.SUCCESS.getCode(), message);
    }

    /**
     * 响应成功
     *
     * @param message 提示信息
     * @param data 响应数据
     * @return Result<T>
     */
    public static <T> Result<T> success(String message, T data){
        return new Result<>(true, ResultCodeEnum.SUCCESS.getCode(), message, data);
    }

    /**
     * 响应失败
     *
     * @param code 状态码
     * @param message 提示信息
     * @return Result<Void>
     */
    public static <T> Result<T> error(Integer code, String message){
        return new Result<>(false, code, message, null);
    }

    /**
     * 扩展数据
     *
     * @param key key
     * @param value value
     * @return Result
     */
    public Result<T> extend(String key, Object value){
        if(null == extend){
            extend = new HashMap<>();
        }
        this.extend.put(key, value);
        return this;
    }
}

