    SUCCESS(20000,"成功"),
    PARAM_ERROR(21003, "参数不正确"),
    ;

    private final Integer code;

	private final String message;

	ResultCodeEnum(Integer code, String message) {
		this.code = code;
		this.message = message;
	}
}

