import java.util.function.Predicate;

/**
 * 异常断言接口
 * 
 * @author hzg
 * @date $!time.currTime("yyyy/MM/dd")
 */
public interface Assert {

    /**
     * 创建异常
     * @return {@link RuntimeException}
     */
    RuntimeException newException();

    /**
     * 创建异常
     * @param code 错误码
     * @param msg 错误提示
     * @return {@link RuntimeException}
     */
    RuntimeException newException(int code, String msg);

    /**
     * 如predicate为true，则抛出异常
     *
     * @param predicate {@link Predicate}
     * @param args 待判断对象
     */
    default <T> void assertException(Predicate<? super T> predicate, T args) {
        if (predicate.test(args)) {
            throw newException();
        }
    }

     /**
     * 抛出自定义异常
     *
     */
    default <T> void assertException() {
        throw newException();
    }

    /**
     * 抛出自定义异常
     *
     * @param code 错误码
     * @param msg 错误提示
     */
    default void assertException(int code, String msg) {
        throw newException(code, msg);
    }
}

