$!callback.setSavePath($tool.append($tableInfo.savePath, "/common/exception"))
##使用宏定义设置包后缀
#setPackageSuffix("common.exception")

import $!{tableInfo.savePackageName}.common.entity.Result;
import $!{tableInfo.savePackageName}.common.enums.ExceptionEnum;
import $!{tableInfo.savePackageName}.common.enums.IExceptionEnum;
import $!{tableInfo.savePackageName}.common.enums.ServletResponseStatusEnum;
import lombok.extern.slf4j.Slf4j;
import org.apache.commons.lang3.StringUtils;
import org.springframework.beans.ConversionNotSupportedException;
import org.springframework.beans.TypeMismatchException;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.support.DefaultMessageSourceResolvable;
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.http.converter.HttpMessageNotReadableException;
import org.springframework.http.converter.HttpMessageNotWritableException;
import org.springframework.validation.BindException;
import org.springframework.validation.FieldError;
import org.springframework.web.HttpMediaTypeNotAcceptableException;
import org.springframework.web.HttpMediaTypeNotSupportedException;
import org.springframework.web.HttpRequestMethodNotSupportedException;
import org.springframework.web.bind.MethodArgumentNotValidException;
import org.springframework.web.bind.MissingPathVariableException;
import org.springframework.web.bind.MissingServletRequestParameterException;
import org.springframework.web.bind.ServletRequestBindingException;
import org.springframework.web.bind.annotation.ExceptionHandler;
import org.springframework.web.bind.annotation.RestControllerAdvice;
import org.springframework.web.context.request.async.AsyncRequestTimeoutException;
import org.springframework.web.multipart.support.MissingServletRequestPartException;
import org.springframework.web.servlet.NoHandlerFoundException;

import javax.validation.ConstraintViolationException;
import java.nio.file.AccessDeniedException;
import java.util.List;
import java.util.stream.Collectors;

/**
 * 统一异常处理类
 *
 * @author hzg
 * @date $!time.currTime("yyyy/MM/dd")
 */
@Slf4j
@RestControllerAdvice
public class GlobalExceptionHandler {

    /**
     * 生产环境
     */
    private final static String ENV_PROD = "prod";

    /**
     * 当前环境
     */
    @Value("$!{valueSign}$!{valuePrefix}spring.profiles.active:dev$!{valuesuffix}")
    private String profile;

    /**
     * Controller上一层相关异常
     *
     * @param ex 异常
     * @return 异常结果
     */
    @ExceptionHandler({
            NoHandlerFoundException.class,
            HttpRequestMethodNotSupportedException.class,
            HttpMediaTypeNotSupportedException.class,
            MissingPathVariableException.class,
            MissingServletRequestParameterException.class,
            TypeMismatchException.class,
            HttpMessageNotWritableException.class,
            HttpMediaTypeNotAcceptableException.class,
            ServletRequestBindingException.class,
            ConversionNotSupportedException.class,
            MissingServletRequestPartException.class,
            AsyncRequestTimeoutException.class
    })
    public ResponseEntity<Result<Integer>> handleExceptionInternal(Exception ex) {
        String message = ex.getMessage();
        Integer status = ServletResponseStatusEnum.of(ex.getClass().getSimpleName()).getStatus();
        log.error("[统一异常]{}异常，异常信息:{}", ex.getClass().getSimpleName(), message);

        if(ENV_PROD.equals(profile)){
            return createResponseResponseEntity(ExceptionEnum.INTERNAL_SERVER_ERROR.getMessage(),
                    ExceptionEnum.INTERNAL_SERVER_ERROR.getCode());
        }

        // 生成返回结果
        return createResponseResponseEntity(message, status);
    }

    /**
     * 参数错误异常处理
     *
     * @param ex HttpMessageNotReadableException
     * @return ResponseEntity
     */
    @ExceptionHandler(HttpMessageNotReadableException.class)
    public ResponseEntity<Result<Integer>> handleHttpMessageNotReadableException(HttpMessageNotReadableException ex) {
        String message = ex.getMessage();
        message = StringUtils.substringBefore(message,":").trim();
        log.error("[统一异常]参数错误异常，异常信息，{}", message);

        // 生成返回结果
        return createResponseResponseEntity(message, HttpStatus.BAD_REQUEST.value());
    }

    /**
     * 处理普通类型Content-Type对象参数校验异常
     *
     * @param ex BindException
     * @return ResponseEntity
     */
    @ExceptionHandler(BindException.class)
    public ResponseEntity<Result<Integer>> handleBindException(BindException ex) {
        List<FieldError> fieldErrors = ex.getFieldErrors();
        String msg = fieldErrors.stream().map(DefaultMessageSourceResolvable::getDefaultMessage)
                .collect(Collectors.joining("|"));
        log.error("[统一异常]对象参数校验异常，异常信息:{}",msg);

        // 生成返回结果
        return createResponseResponseEntity(msg, HttpStatus.BAD_REQUEST.value());
    }

    /**
     * 处理Content-Type=application/json的对象参数校验异常
     *
     * @param ex MethodArgumentNotValidException
     * @return ResponseEntity
     */
    @ExceptionHandler(MethodArgumentNotValidException.class)
    public ResponseEntity<Result<Integer>> handleMethodArgumentNotValidException(MethodArgumentNotValidException ex) {
        List<FieldError> fieldErrors = ex.getBindingResult().getFieldErrors();
        String msg = fieldErrors.stream().map(DefaultMessageSourceResolvable::getDefaultMessage)
                .collect(Collectors.joining("|"));
        log.error("[统一异常]对象参数校验异常，异常信息:{}",msg);

        // 生成返回结果
        return createResponseResponseEntity(msg, HttpStatus.BAD_REQUEST.value());
    }

    /**
     * 处理单个参数校验异常
     *
     * @param e ConstraintViolationException
     * @return ResponseEntity
     */
    @ExceptionHandler(ConstraintViolationException.class)
    public ResponseEntity<Result<Integer>> handleConstraintViolationException(ConstraintViolationException e) {
        String message = e.getMessage();
        message = StringUtils.substringAfterLast(message,":").trim();
        log.error("[统一异常]单个参数校验异常，异常信息:{}",message);

        // 生成返回结果
        return createResponseResponseEntity(message, HttpStatus.BAD_REQUEST.value());
    }

    /**
     * 处理403异常
     *
     * @param e AccessDeniedException
     * @return ResponseEntity
     */
    @ExceptionHandler(AccessDeniedException.class)
    public ResponseEntity<Result<Integer>> handleAccessDeniedException(AccessDeniedException e) {
        String message = e.getMessage();
        log.error("[统一异常]拒绝访问异常，异常信息，{}", message);

        // 生成返回结果
        return createResponseResponseEntity(message, HttpStatus.FORBIDDEN.value());
    }

    /**
     * 处理自定义业务异常
     *
     * @param e ServiceException
     * @return ResponseEntity
     */
    @ExceptionHandler(ServiceException.class)
    public ResponseEntity<Result<Integer>> handleServiceException(ServiceException e){
        String message;
        int code;
        IExceptionEnum exceptionEnum = e.getExceptionEnum();
        if(null != exceptionEnum){
            message = exceptionEnum.getMessage();
            code = exceptionEnum.getCode();
        }else {
            message = e.getMessage();
            code = e.getCode();
        }

        log.error("[统一异常]ServiceException异常，异常信息:{}", message);

        // 生成返回结果
        return createResponseResponseEntity(message, code);
    }

    /**
     * 其他异常统一处理
     *
     * @param e Exception
     * @return ResponseEntity
     */
    @ExceptionHandler(Exception.class)
    public ResponseEntity<Result<Integer>> exceptionHandler(Exception e) {
        log.error("[统一异常]其他异常，异常:{}", e.getClass().toString());
        String message = e.getMessage();
        log.error("[统一异常]其他异常，异常信息，{}", message);

        if (ENV_PROD.equals(profile)) {
            return createResponseResponseEntity(ExceptionEnum.INTERNAL_SERVER_ERROR.getMessage(),
                    ExceptionEnum.INTERNAL_SERVER_ERROR.getCode());
        }

        // 生成返回结果
        return createResponseResponseEntity(message, HttpStatus.INTERNAL_SERVER_ERROR.value());
    }

    /**
     * 返回异常处理结果
     *
     * @param msg 异常信息
     * @param status 状态码
     * @return ResponseEntity
     */
    private ResponseEntity<Result<Integer>> createResponseResponseEntity(String msg, int status) {
        // 生成返回结果
        Result<Integer> result = new Result<>(false, status, msg);
        return ResponseEntity.status(status).body(result);
    }
}

