$!callback.setSavePath($tool.append($tableInfo.savePath, "/mapping/", $!{packageName}))

#if($tableInfo.savePackageName)package $!{tableInfo.savePackageName}.#{end}mapping.$!{packageName};

import $!{tableInfo.savePackageName}.common.mapping.BaseMapping;
import $!{tableInfo.savePackageName}.pojo.$!{tableInfo.name};
import $!{tableInfo.savePackageName}.vo.$!{tableInfo.name}Vo;
import org.mapstruct.Mapper;
import org.mapstruct.Mapping;
import org.mapstruct.Mappings;
import org.mapstruct.ReportingPolicy;

import java.util.List;

#tableComment("VoMapping")
